		  to_char(a.COMMIT_TIME,'yyyy-mm-dd HH24:mi:ss') COMMIT_TIME,to_char(a.RETURN_VISIT_TIME,'yyyy-mm-dd HH24:mi:ss') RETURN_VISIT_TIME, a.PHONE,CUST_ADDRESS, rownum rn from  
		  t_order a where  ORDER_CHANNEL='71'

			 AND ORDER_STATUS in (${orderStatus})


			 AND OPR_DEPT_ID = #{oprDeptID} 
			 AND 
			 (OPR_ID is null OR  OPR_ID=#{oprID} )


			 AND OPR_ID = #{oprID} 


			 AND a.CREAT_TIME >= #{beginTime} 


		  	 AND a.CREAT_TIME <= #{endTime}


			 AND ORDER_TYPE = #{order_type} 


			 AND FIX_TYPE = #{fix_type} 


			 AND b.XQ_CODE = #{fix_xq} 

		   order by a.creat_time desc
		) t

select count(1) TOTAL from
t_order where ORDER_CHANNEL='70'

	 AND ORDER_STATUS = #{orderStatus} 


	 AND OPR_DEPT_ID = #{oprDeptID} 


 AND OPR_ID = #{oprID}

select MAXACCEPT,CODE_KEY,CODE_ID,CODE_NAME,CODE_DES from t_common_code where 1=1 

	AND CODE_KEY=#{codeKey}


	 AND CODE_ID in (${codeIDS})

select to_char(creat_time,'yyyy-mm-dd') month, count(*) num from t_order where 1=1 

	and fixuser_id = #{userId} 


	and creat_time >= #{creatTime} 


	and creat_time <= #{overTime} 

group by to_char(creat_time,'yyyy-mm-dd') order by to_char(creat_time,'yyyy-mm-dd') asc

select to_char(creat_time,'yyyy-mm-dd') month, count(*) num from t_order where order_status in('25', '26') 

	and fixuser_id = #{userId} 


	and creat_time >= #{creatTime} 


	and creat_time <= #{overTime} 

group by to_char(creat_time,'yyyy-mm-dd') order by to_char(creat_time,'yyyy-mm-dd') asc

select to_char(creat_time,'yyyy-mm-dd') month, count(*) num from t_order where order_status = '23' 

	and fixuser_id = #{userId} 


	and creat_time >= #{creatTime} 


	and creat_time <= #{overTime} 

group by to_char(creat_time,'yyyy-mm-dd') order by to_char(creat_time,'yyyy-mm-dd') asc

select a.maxaccept, a.user_account, a.user_pwd, a.dept_code, a.user_name, a.phone, a.address, a.user_role, a.vos_account, a.vos_pwd, b.role_name, b.role_menus, b.role_des, b.role_level from t_user a, t_role b where a.user_role=b.maxaccept
and a.dept_code=#{deptCode}
and b.role_level=#{roleLevel}

select to_char(creat_time,'yyyy-mm-dd') month, count(*) num from t_order where order_dgree = '32'

	and fix_dept_id = (select dept_code from t_user where maxaccept = #{managerId}) 


	and fixuser_id = #{userId} 


	and creat_time >= #{creatTime} 


	and creat_time <= #{overTime} 

group by to_char(creat_time,'yyyy-mm-dd') order by to_char(creat_time,'yyyy-mm-dd') asc

select a.MAXACCEPT, a.ORDER_TYPE, (select CODE_NAME from t_common_code where code_key='ORDER_TYPE' and code_id=a.order_type) ORDER_TYPE_NAME, a.FIX_TYPE, (select CODE_NAME from t_common_code where code_key='FIX_TYPE' and code_id=a.FIX_TYPE) FIX_TYPE_NAME, a.OPR_ID,a.OPR_NAME,a.OPR_DEPT_ID,a.CUST_ID,a.CUST_NAME, a.ORDER_STATUS,(select CODE_NAME from t_common_code where code_key='ORDER_STATUS' and code_id=a.ORDER_STATUS) ORDER_STATUS_NAME, a.FIXUSER_ID,a.FIXUSER_NAME,a.FIX_DEPT_ID, a.ORDER_DGREE, (select CODE_NAME from t_common_code where code_key='ORDER_DGREE' and code_id=a.ORDER_DGREE) ORDER_DGREE_NAME, a.ORDER_MARK,to_char(a.CREAT_TIME,'yyyy-mm-dd HH24:mi:ss') CREAT_TIME,a.UPDATE_TIME,a.FIX_MARK, to_char(a.TOFIXDEPT_TIME,'yyyy-mm-dd HH24:mi:ss') TOFIXDEPT_TIME,to_char(a.TOFIXPERSON_TIME,'yyyy-mm-dd HH24:mi:ss') TOFIXPERSON_TIME, to_char(a.COMMIT_TIME,'yyyy-mm-dd HH24:mi:ss') COMMIT_TIME,to_char(a.RETURN_VISIT_TIME,'yyyy-mm-dd HH24:mi:ss') RETURN_VISIT_TIME, a.PHONE,CUST_ADDRESS, rownum rn from t_order a where 1=1
and ORDER_CHANNEL = #{orderChannel}
and PHONE = #{phone}
and maxaccept = ${maxaccept}

select MAXACCEPT,ORDER_TYPE,ORDER_TYPE_NAME,FIX_TYPE,FIX_TYPE_NAME,OPR_ID,OPR_NAME,OPR_DEPT_ID,CUST_ID,CUST_NAME,ORDER_STATUS,ORDER_STATUS_NAME,FIXUSER_ID,FIXUSER_NAME,FIX_DEPT_ID,ORDER_DGREE, ORDER_DGREE_NAME,ORDER_MARK,CREAT_TIME,UPDATE_TIME,FIX_MARK,TOFIXDEPT_TIME,TOFIXPERSON_TIME,COMMIT_TIME,RETURN_VISIT_TIME,PHONE, CUST_ADDRESS, FIX_DEPT_NAME from ( select a.MAXACCEPT, a.ORDER_TYPE, (select CODE_NAME from t_common_code where code_key='ORDER_TYPE' and code_id=a.order_type) ORDER_TYPE_NAME, a.FIX_TYPE, (select CODE_NAME from t_common_code where code_key='FIX_TYPE' and code_id=a.FIX_TYPE) FIX_TYPE_NAME, a.OPR_ID,a.OPR_NAME,a.OPR_DEPT_ID,a.CUST_ID,a.CUST_NAME, a.ORDER_STATUS,(select CODE_NAME from t_common_code where code_key='ORDER_STATUS' and code_id=a.ORDER_STATUS) ORDER_STATUS_NAME, a.FIXUSER_ID,a.FIXUSER_NAME,a.FIX_DEPT_ID,(select DEPT_NAME from t_dept x where x.MAXACCEPT = a.FIX_DEPT_ID) FIX_DEPT_NAME, a.ORDER_DGREE, (select CODE_NAME from t_common_code where code_key='ORDER_DGREE' and code_id=a.ORDER_DGREE) ORDER_DGREE_NAME, a.ORDER_MARK,to_char(a.CREAT_TIME,'yyyy-mm-dd HH24:mi:ss') CREAT_TIME,a.UPDATE_TIME,a.FIX_MARK, to_char(a.TOFIXDEPT_TIME,'yyyy-mm-dd HH24:mi:ss') TOFIXDEPT_TIME,to_char(a.TOFIXPERSON_TIME,'yyyy-mm-dd HH24:mi:ss') TOFIXPERSON_TIME, to_char(a.COMMIT_TIME,'yyyy-mm-dd HH24:mi:ss') COMMIT_TIME,to_char(a.RETURN_VISIT_TIME,'yyyy-mm-dd HH24:mi:ss') RETURN_VISIT_TIME, a.PHONE,CUST_ADDRESS, rownum rn from t_order a where ORDER_STATUS = '26'
AND ORDER_CHANNEL=#{orderChannel}
AND a.CREAT_TIME >= #{creatTime}
AND a.CREAT_TIME <= #{overTime}
AND FIX_TYPE = #{fixType}
AND a.OPR_DEPT_ID = (select dept_code from t_user where maxaccept = #{kfManagerId})
order by a.creat_time desc ) t
